  `class_id` int(3) NOT NULL default '0',
  `lvl` int(3) NOT NULL default '0',
  `hp` FLOAT NOT NULL default '0.0',
  `cp` FLOAT NOT NULL default '0.0',
  `mp` FLOAT NOT NULL default '0.0',
  PRIMARY KEY  (`class_id`,`lvl`)
) ENGINE=MyISAM;

